<?phprequire_once ("clases/empleado.php");require_once ("clases/archivo.php");$queHago = isset($_POST['queHago']) ? $_POST['queHago'] : NULL;switch($queHago){	case "mostrarGrilla":			$ArrayDeEmpleados = Empleado::TraerTodosLosEmpleados();		$grilla = '<table class="table">					<thead style="background:rgb(14, 26, 112);color:#fff;">						<tr>							<th>  LEGAJO     </th>							<th>  NOMBRE     </th>							<th>  APELLIDO   </th>                            <th>  SEXO       </th>							<th>  FOTO       </th>							<th>  ACCION     </th>						</tr> 					</thead>';   			foreach ($ArrayDeEmpleados as $prod){			$empleado = array();			$empleado["legajo"] = $prod->GetLegajo();			$empleado["nombre"] = $prod->GetNombre();            $empleado["apellido"] = $prod->GetApellido();            $empleado["rdoSexo"] = $prod->GetSexo();			$empleado = json_encode($empleado);					$grilla .= "<tr>							<td>".$prod->GetLegajo()."</td>							<td>".$prod->GetNombre()."</td>							<td>".$prod->GetApellido()."</td>							<td>".$prod->GetSexo()."</td>							<td><img src='archivos/".$prod->GetFoto()."' width='100px' height='100px'/></td>							<td><input type='button' value='Eliminar' class='MiBotonUTN' id='btnEliminar' onclick='EliminarEmpleado($empleado)' />								<input type='button' value='Modificar' class='MiBotonUTN' id='btnModificar' onclick='ModificarEmpleado($empleado)' /></td>						</tr>";		}				$grilla .= '</table>';						echo $grilla;		break;			case "subirFoto":				$res = Archivo::Subir();				echo json_encode($res);		break;		case "borrarFoto":				$pathFoto = isset($_POST['foto']) ? $_POST['foto'] : NULL;		$res["Exito"] = Archivo::Borrar("./tmp/".$pathFoto);				echo json_encode($res);				break;			case "agregar":		$retorno["Exito"] = TRUE;		$retorno["Mensaje"] = "";		$obj = isset($_POST['empleado']) ? json_decode(json_encode($_POST['empleado'])) : NULL;				$p = new Empleado($obj->legajo,$obj->nombre,$obj->apellido,$obj->rdoSexo,$obj->archivo);				if(!Empleado::Guardar($p)){			$retorno["Exito"] = FALSE;			$retorno["Mensaje"] = "Lamentablemente ocurrio un error y no se pudo escribir en el archivo.";		}		else{			if(!Archivo::Mover("./tmp/".$obj->archivo, "./archivos/".$obj->archivo)){				$retorno["Exito"] = FALSE;				$retorno["Mensaje"] = "Lamentablemente ocurrio un error al mover el archivo del repositorio temporal al repositorio final.";			}			else{				$retorno["Mensaje"] = "El archivo fue escrito correctamente. PRODUCTO agregado CORRECTAMENTE!!!";			}		}			echo json_encode($retorno);				break;		case "eliminar":		$retorno["Exito"] = TRUE;		$retorno["Mensaje"] = "";		$obj = isset($_POST['empleado']) ? json_decode(json_encode($_POST['empleado'])) : NULL;				if(!Empleado::Eliminar($obj->legajo)){			$retorno["Exito"] = FALSE;			$retorno["Mensaje"] = "Lamentablemente ocurrio un error y no se pudo escribir en el archivo.";		}		else{			$retorno["Mensaje"] = "El archivo fue escrito correctamente. PRODUCTO eliminado CORRECTAMENTE!!!";		}			echo json_encode($retorno);				break;			case "modificar":		$retorno["Exito"] = TRUE;		$retorno["Mensaje"] = "";		$obj = isset($_POST['empleado']) ? json_decode(json_encode($_POST['empleado'])) : NULL;				$p = new Empleado($obj->legajo,$obj->nombre,$obj->apellido,$obj->rdoSexo,$obj->archivo);				if(!Empleado::Modificar($p)){			$retorno["Exito"] = FALSE;			$retorno["Mensaje"] = "Lamentablemente ocurrio un error y no se pudo escribir en el archivo.";		}		else{			if(!Archivo::Mover("./tmp/".$obj->archivo, "./archivos/".$obj->archivo)){				$retorno["Exito"] = FALSE;				$retorno["Mensaje"] = "Lamentablemente ocurrio un error al mover el archivo del repositorio temporal al repositorio final.";			}			else{				$retorno["Mensaje"] = "El archivo fue escrito correctamente. PRODUCTO modificado CORRECTAMENTE!!!";			}		}			echo json_encode($retorno);				break;	default:		echo ":(";}?>